fun lookupCountryISOCode(ISOCode) = {
	code: if (ISOCode == 'US') 'USA' else p('wday.country')	
}

fun createCountryRegionReference(countryISOCode, stateISOCode) = {
	code: lookupCountryISOCode(countryISOCode).code ++ "-" ++	stateISOCode
}

